//140 x 140

//      N
//      ^
// W <- + -> E
//      v
//      S

$xmasCount = 0;

//prep array of arrayed characters 140 x 140
foreach(explode("\n", $input) as $row) {
    $wordArray[] = str_split($row);
}

function masCheck(array $input): bool
{
    $word = implode($input);

    return $word == 'MAS';
}

function checkForMas(
    array $data,
    array $coords,
): bool {
    [$x, $y] = $coords;

    $firstDiagonal = [$data[$y - 1][$x - 1], $data[$y][$x], $data[$y + 1][$x + 1]];
    $secondDiagonal = [$data[$y - 1][$x + 1], $data[$y][$x], $data[$y + 1][$x - 1]];

    if (masCheck($firstDiagonal) && masCheck($secondDiagonal)) {
        return true;
    } elseif (masCheck($firstDiagonal) && masCheck(array_reverse($secondDiagonal))) {
        return true;
    } elseif (masCheck($secondDiagonal) && masCheck(array_reverse($firstDiagonal))) {
        return true;
    } elseif (masCheck(array_reverse($firstDiagonal)) && masCheck(array_reverse($secondDiagonal))) {
        return true;
    }

    return false;
}

$min = 1;
$max = 138;
foreach($wordArray as $row => $rowData) {
    $xLocationsToCheck = array_keys($rowData, 'A');
    foreach($xLocationsToCheck as $xLocation) {
        if (($row >= $min) && ($row <= $max)) {
            if (($xLocation >= $min) && ($xLocation <= $max)) {
                if (checkForMas($wordArray, [$xLocation, $row])) {
                    $xmasCount++;
                };
            }
        }
    }
}

echo "$xmasCount \n";